package practicejava;

public class loopifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		if(a<10)
		{
			System.out.println(true);
		}
		else if(a>10)
		{
			System.out.println(false);
		}
		else if(a==10)
		{
		    System.out.println("equal");	
		}
	}

}
